import { Component, OnInit } from '@angular/core';
import { GroceryService} from 'src/app/services/grocery.service';
import { grocery} from 'src/app/shared/models/grocery';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  grocery:grocery[] = [];
  constructor(private groceryService:GroceryService) {
    this.grocery = groceryService.getAll();
  }

  ngOnInit(): void {
  }

}
<ul>
    <li *ngFor="let grocery of grocery">
      <a routerLink="/grocery/{{grocery.id}}">
        <img [src]="grocery.imageUrl" [alt]="grocery.name"/>
        <div class="content">
          <div class="name">
            {{grocery.name}}
          </div>
          <span class="favorite {{grocery.favorite?'':'not'}}">
            ❤
          </span>
          <div class="stars">
            <star-rating
            [value]="grocery.stars"
            [totalstars]="5"
            checkedcolor="red"
            uncheckedcolor="black"
            size="22px"
            [readonly]="true">
          </star-rating>
          </div>
          <div class="product-item-footer">
            <div class="origins">
              <span *ngFor="let origin of grocery.origins">
                {{origin}}
              </span>
            </div>


          <div class="price">
            <span>
              {{grocery.price | Currency}}
            </span>
          </div>
        </div>
